INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('133', 'Hunter S. Thompson', '7/18/1937', '2/20/2005', 'Hunter Stockton Thompson was an American journalist and author, famous for his book Fear and Loathing in Las Vegas. He is credited as the creator of Gonzo journalism, a style of reporting where reporters involve themselves in the action to such a degree that they become the central figures of their stories. He is also known for his promotion and use of psychedelics and other mind-altering substances (and to a lesser extent, alcohol and firearms), his libertarian views, and his iconoclastic contempt for authority. He committed suicide in 2005.');
INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('134', 'Joelle Charbonneau', '11/4/1974', NULL, 'I am a storyteller at heart. I have performed in a variety of operas, musical theatre and children''s theatre productions across the Chicagoland area. While I''m happy to perform for an audience, I am equally delighted to teach private voice lessons and use my experience from the stage to create compelling characters on the page. I am the author of the Rebecca Robbins mystery series (Minotaur Books), The Paige Marshall Glee Club mysteries (Berkley) and The Testing YA triology (Houghton Mifflin Harcourt). I am a storyteller at heart. I have performed in a variety of operas, musical theatre and children''s theatre productions across the Chicagoland area. While I''m happy to perform for an audience, I am equally delighted to teach private voice lessons and use my experience from the stage to create compelling characters on the page. I am the author of the Rebecca Robbins mystery series (Minotaur Books), The Paige Marshall Glee Club mysteries (Berkley) and The Testing YA triology (Houghton Mifflin Harcourt). ...more');
