INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('1172', 'Richard Laymon', '1/14/1947', '2/14/2001', 'Richard Laymon was born in Chicago and grew up in California. He earned a BA in English Literature from Willamette University, Oregon and an MA from Loyola University, Los Angeles. He worked as a schoolteacher, a librarian, and a report writer for a law firm, and was the author of more than thirty acclaimed novels. He also published more than sixty short stories in magazines such as Ellery Queen, Alfred Hitchcock, and Cavalier, and in anthologies including Modern Masters of Horror.He died from a massive heart attack on February 14, 2001 (Valentine''s Day). Also published under the name Richard Kelly Richard Laymon was born in Chicago and grew up in California. He earned a BA in English Literature from Willamette University, Oregon and an MA from Loyola University, Los Angeles. He worked as a schoolteacher, a librarian, and a report writer for a law firm, and was the author of more than thirty acclaimed novels. He also published more than sixty short stories in magazines such as Ellery Queen, Alfred Hitchcock, and Cavalier, and in anthologies including Modern Masters of Horror.He died from a massive heart attack on February 14, 2001 (Valentine''s Day). Also published under the name Richard Kelly ...more');
INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('1173', 'Hans Rosling', '7/27/1948', '2/7/2017', 'Hans Rosling (1948 – 2017) was a Swedish physician, academic, statistician, and public speaker.He was the Professor of International Health at Karolinska Institute and was the co-founder and chairman of the Gapminder Foundation, which developed the Trendalyzer software. He held presentations around the world, including several TED Talks in which he promoted the use of data to explore development issues.');
INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('1174', 'Mimi Jean Pamfiloff', NULL, NULL, 'Because Mimi wants to focus on writing and her freakin'' awesome fans, she does NOT monitor this site. PLEASE GO TO her Facebook FAN PAGE: https://www.facebook.com/groups/MimiJ...or her website: www.mimijean.net, and sign up for her newsletter if you want the latest FREEBIES and news!****MIMI JEAN PAMFILOFF is a New York Times bestselling author who writes insane plot twists that will have you burning through the pages. Whether it’s Romance, Suspense/Thriller, or Fantasy, there are always big heroes to root for, smart and resourceful heroines, and a ton of heart pumping excitement in every story.  Mimi lives with her extremely patient husband (“Be right there! Just one more page, honey!”), two pirates-in-training (their boys), and their thre Because Mimi wants to focus on writing and her freakin'' awesome fans, she does NOT monitor this site. PLEASE GO TO her Facebook FAN PAGE: https://www.facebook.com/groups/MimiJ...or her website: www.mimijean.net, and sign up for her newsletter if you want the latest FREEBIES and news!****MIMI JEAN PAMFILOFF is a New York Times bestselling author who writes insane plot twists that will have you burning through the pages. Whether it’s Romance, Suspense/Thriller, or Fantasy, there are always big heroes to root for, smart and resourceful heroines, and a ton of heart pumping excitement in every story.  Mimi lives with her extremely patient husband (“Be right there! Just one more page, honey!”), two pirates-in-training (their boys), and their three spunky dragons (really, just very tiny dogs with big attitudes) Snowy, Mini, and Mack, in the vampire-unfriendly state of Arizona. ...more');
INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('1175', 'Roy Huff', NULL, NULL, 'Roy Huff is a Hawaii-based best-selling author, peer-reviewed research scientist, and teacher. After overcoming significant childhood poverty in the US mainland, he moved to the islands and hasn''t looked back.Despite his challenges, which include a family stricken by mental illness and the loss of a father to HIV/AIDS, he’s earned five degrees, trained on geostationary satellites for NASA''s GOES-R Proving Ground, and has written numerous bestsellers.Roy embraces optimism, science, and creativity and hopes you’ll come along for the amazing ride. You can download Roy Huff''s free sci-fi short at https://www.royhuff.net/salvationship or connect with him on Twitter, Facebook, &amp; Instagram @realroyhuff Roy Huff is a Hawaii-based best-selling author, peer-reviewed research scientist, and teacher. After overcoming significant childhood poverty in the US mainland, he moved to the islands and hasn''t looked back.Despite his challenges, which include a family stricken by mental illness and the loss of a father to HIV/AIDS, he’s earned five degrees, trained on geostationary satellites for NASA''s GOES-R Proving Ground, and has written numerous bestsellers.Roy embraces optimism, science, and creativity and hopes you’ll come along for the amazing ride. You can download Roy Huff''s free sci-fi short at https://www.royhuff.net/salvationship or connect with him on Twitter, Facebook, &amp; Instagram @realroyhuff ...more');
