INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('1177', 'Justina Chen', NULL, NULL, 'Justina Chen is an award-winning writer. Her latest book, THE ART OF INSPIRATION: LEAD YOUR BEST STORY, is for any leader who wants to inspire employees, customers, and constituents. Her YA novel, NORTH OF BEAUTIFUL, was named to Best Books lists by Kirkus Reviews and Barnes &amp; Noble. Her other novels include A Blind Spot for Boys, Return to Me, Girl Overboard (a Junior Library Guild premiere selections) and Nothing but the Truth (and a few white lies), which won the Asian Pacific American Award for Literature. Additionally, Justina co-founded readergirlz, a cutting-edge literacy and social media project for teens, which won the National Book Foundation''s Award for Innovations in Reading. When she isn’t writing for teens, Justina is a story Justina Chen is an award-winning writer. Her latest book, THE ART OF INSPIRATION: LEAD YOUR BEST STORY, is for any leader who wants to inspire employees, customers, and constituents. Her YA novel, NORTH OF BEAUTIFUL, was named to Best Books lists by Kirkus Reviews and Barnes &amp; Noble. Her other novels include A Blind Spot for Boys, Return to Me, Girl Overboard (a Junior Library Guild premiere selections) and Nothing but the Truth (and a few white lies), which won the Asian Pacific American Award for Literature. Additionally, Justina co-founded readergirlz, a cutting-edge literacy and social media project for teens, which won the National Book Foundation''s Award for Innovations in Reading. When she isn’t writing for teens, Justina is a story strategist who helps good leaders become great leaders through storytelling. Formerly, she was the speechwriter for a president of Microsoft Entertainment &amp; Devices. She conducts popular storytelling workshops and has presented at organizations around the world. ...more');
INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('1178', 'Anthony Marra', NULL, NULL, 'Anthony Marra is the New York Times bestselling author of The Tsar of Love and Techno and A Constellation of Vital Phenomena, winner of the National Book Critics Circle’s John Leonard Prize and the Anisfield-Wolf Book Award, and longlisted for the National Book Award.');
INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('1179', 'G.D. Falksen', NULL, NULL, 'G. D. Falksen is the author of "The Secret Life of Kitty Granger" (Carolrhoda Lab, 2021), "Maiden of War" (Audible Originals, 2020), "Doctor Cthulittle" (Insight Studios, 2018), "The Transatlantic Conspiracy" (Soho Teen, 2016), and "The Ouroboros Cycle" series (Wildside Press, 2013 to present), and has been a contributor to the award-winning anthologies "Mine!" and "Cthulhu Fhtagn!". He is also Chairman of the Advisory Board of Writers and Artists Across the Country, a non-profit organization dedicated to facilitating author visits to underserved schools. Falksen’s "Ouroboros Cycle" series is currently being developed for television. G. D. Falksen is the author of "The Secret Life of Kitty Granger" (Carolrhoda Lab, 2021), "Maiden of War" (Audible Originals, 2020), "Doctor Cthulittle" (Insight Studios, 2018), "The Transatlantic Conspiracy" (Soho Teen, 2016), and "The Ouroboros Cycle" series (Wildside Press, 2013 to present), and has been a contributor to the award-winning anthologies "Mine!" and "Cthulhu Fhtagn!". He is also Chairman of the Advisory Board of Writers and Artists Across the Country, a non-profit organization dedicated to facilitating author visits to underserved schools. Falksen’s "Ouroboros Cycle" series is currently being developed for television. ...more');
