INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('1251', 'Miquel Reina', NULL, NULL, 'Barcelona-born Miquel Reina defines himself as a dreamer and a fighter; from a young age, he was drawn to the creative life, studying design and cinema before building his reputation as a filmmaker and graphic artist. His work in advertising won him several awards, including the prestigious Bronze Sun at Spain’s Festival de San Sebastián in 2011, and in 2014, his music video “Dead in the Water” was screened at the Tribeca Film Festival. Reina has lived in Vancouver, Canada, since 2016, working for a video production studio and dedicating his free time to his most gratifying passion: writing.  Lights on the Sea is his debut novel, translated in 5 different languages. You can find more of his work at miquelreinabooks.com Barcelona-born Miquel Reina defines himself as a dreamer and a fighter; from a young age, he was drawn to the creative life, studying design and cinema before building his reputation as a filmmaker and graphic artist. His work in advertising won him several awards, including the prestigious Bronze Sun at Spain’s Festival de San Sebastián in 2011, and in 2014, his music video “Dead in the Water” was screened at the Tribeca Film Festival. Reina has lived in Vancouver, Canada, since 2016, working for a video production studio and dedicating his free time to his most gratifying passion: writing.  Lights on the Sea is his debut novel, translated in 5 different languages. You can find more of his work at miquelreinabooks.com ...more');
INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('1252', 'Robert   Harris', NULL, NULL, 'ROBERT HARRIS is the author of nine best-selling novels: Fatherland, Enigma, Archangel, Pompeii, Imperium, The Ghost Writer, Conspirata, The Fear Index, and An Officer and a Spy. Several of his books have been adapted to film, most recently The Ghost Writer, directed by Roman Polanski. His work has been translated into thirty-seven languages. He lives in the village of Kintbury, England, with his wife, Gill Hornby.');
