INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('1618', 'Jessica Sorensen', NULL, NULL, 'Jessica Sorensen is a New York Times and USA Today bestselling author from the snowy mountains of Wyoming. When she''s not writing, she spends her time reading and hanging out with her family.http://www.jessicasorensen.comhttp://www.facebook.com/pages/Jessica... Jessica Sorensen is a New York Times and USA Today bestselling author from the snowy mountains of Wyoming. When she''s not writing, she spends her time reading and hanging out with her family.http://www.jessicasorensen.comhttp://www.facebook.com/pages/Jessica... ...more');
INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('1619', 'Emma Chase', NULL, NULL, 'Emma Chase is a New York Times and USA Today bestselling author of romance filled with humor, heat and heart. Her books have been published in over 20 languages and the film adaptation of Tangled, Emma''s iconic enemies-to-lovers rom-com, was recently released by Passionflix. Emma lives happily-ever-after in New Jersey with her husband, two children, and two adorable, misbehaving dogs. Emma Chase is a New York Times and USA Today bestselling author of romance filled with humor, heat and heart. Her books have been published in over 20 languages and the film adaptation of Tangled, Emma''s iconic enemies-to-lovers rom-com, was recently released by Passionflix. Emma lives happily-ever-after in New Jersey with her husband, two children, and two adorable, misbehaving dogs. ...more');
