INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('2140', 'Stephen Graham Jones', NULL, NULL, 'Stephen Graham Jones is the NYT bestselling author of twenty-five or thirty books. He really likes werewolves and slashers. Favorite novels change daily, but Valis and Love Medicine and Lonesome Dove and It and The Things They Carried are all usually up there somewhere. Stephen lives in Boulder, Colorado. It''s a big change from the West Texas he grew up in. He''s married with a couple kids, and probably one too many trucks. Stephen Graham Jones is the NYT bestselling author of twenty-five or thirty books. He really likes werewolves and slashers. Favorite novels change daily, but Valis and Love Medicine and Lonesome Dove and It and The Things They Carried are all usually up there somewhere. Stephen lives in Boulder, Colorado. It''s a big change from the West Texas he grew up in. He''s married with a couple kids, and probably one too many trucks. ...more');
INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('2141', 'Steven  Bruce', NULL, NULL, 'Steven Bruce is a poet, writer, and award-winning author. His poetry and short stories have appeared in magazines, webzines, and anthologies worldwide. In 2018, he graduated from Teesside University with a Master''s Degree in Creative Writing. He is the recipient of the Indies Today Five-star Recommendation Badge. Born in the North of England, he now lives and writes full-time out of an apartment in Barcelona.Social media: Official Website │ LinkedIn Steven Bruce is a poet, writer, and award-winning author. His poetry and short stories have appeared in magazines, webzines, and anthologies worldwide. In 2018, he graduated from Teesside University with a Master''s Degree in Creative Writing. He is the recipient of the Indies Today Five-star Recommendation Badge. Born in the North of England, he now lives and writes full-time out of an apartment in Barcelona.Social media: Official Website │ LinkedIn ...more');
INSERT INTO Author (id, name, birth_date, death_date, about) VALUES ('2142', 'Leonora Carrington', '4/6/1917', '5/25/2011', 'Leonora Carrington was an English-born Mexican artist, surrealist painter, and novelist. She lived most of her adult life in Mexico City, and was one of the last surviving participants in the Surrealist movement of the 1930s. Carrington was also a founding member of the Women’s Liberation Movement in Mexico during the 1970s.');
